import React, { Component, useState } from "react";
// import React from 'react'
export default function Navigation() {
    const [isSelected,SetSelected]=useState(0)
  return (
            <div>
                <nav>
                    <ul className="nav">
                        <li className={`nav-ele ${isSelected == 0 ? "active": "" }`}  onClick={()=>SetSelected(0)}><a href="#home"><span class="text-dec-none">Home</span></a></li>
                        <li   className={`nav-ele ${isSelected == 1 ? "active": "" }`}><a herf="#news">News</a></li>
                        <li   className={`nav-ele ${isSelected == 2 ? "active": "" }`}><a herf="#about">About</a></li>
                        <li  className={`nav-ele ${isSelected == 3 ? "active": "" }`}><a herf="#contact">Contact</a></li>
                    </ul>
                </nav>
            </div>
  )
}

